def input
  <<~IN
    
    
    
    
    
  IN
end

class DoorCode
  POSITIONS = 9.times.map{|i| i+1}

  def initialize
    @position = 4
    @code = []
  end

  def handle_command(command)
    command.split('').each do |dir|
      move_dir(dir)
    end
    add_position_to_code
  end

  def move_dir(dir)
    case dir
    when 'U'
      unless @position / 3 == 0
        @position -= 3
      end
    when 'D'
      unless @position / 3 == 2
        @position += 3
      end
    when 'L'
      unless @position % 3 == 0
        @position -= 1
      end
    when 'R'
      unless @position % 3 == 2
        @position += 1
      end
    end
  end

  def add_position_to_code
    @code << POSITIONS[@position]
  end

  def output_code
    puts "The code is: #{@code.join('')}"
  end
end

doorcode = DoorCode.new
input.chomp.split.each do |command|
  doorcode.handle_command command
end

doorcode.output_code
